"""Hold state of mouse, for interacting with cv2 windows."""import cv2class MouseState(object):    MOUSE_BUTTON_EVENTS = {cv2.EVENT_LBUTTONDOWN: "l-down",                           cv2.EVENT_LBUTTONUP: "l-up",                           cv2.EVENT_RBUTTONDOWN: "r-down",                           cv2.EVENT_RBUTTONUP: "r-up",                           cv2.EVENT_MBUTTONDOWN: "m-down",                           cv2.EVENT_MBUTTONUP: "m-up"}    def __init__(self):        self._x, self._y = None, None        self._last_x, self._last_y = None, None    def update_state(self, event, x, y, flags, param):        """        Call this inside your CV2 mouse event callback, with the same params        """        self._last_x, self._last_y = self._x, self._y        self._x, self._y = x, y        motion, position, button_change = None, None, None        if self._x is not None:            position = self._x, self._y            if self._last_x is not None:                motion = self._x - self._last_x, self._y - self._last_y        if event in self.MOUSE_BUTTON_EVENTS:            button_change = self.MOUSE_BUTTON_EVENTS[event]        return position, motion, button_change